var oop = require('oop').oop;



var NodeType = oop.Class({
	name: 'Node Type',

	schema: {
		inputToSpec: {
			'String': 'language'
		},
		properties: [
			{
				names: ['language', 'lang']
			},
			{
				names: ['element', 'elmt']
			}
		]
	},
	statics: {
		toJSON: function (type) {
			return type.language == null || type.element == null ? "" : type.language + ":" + type.element;
		}
	}
});



exports.NodeType = NodeType;
